import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css'],
})
export class SignupComponent {
  usernameError: string = '';
  passwordError: string = '';

  constructor(private router: Router) {}

  async getData(username: any, password: any) {
    this.usernameError = username ? '' : 'Username is required';
    this.passwordError = password ? '' : 'Password is required';

    if (username && password) {
      var myHeaders = new Headers();
      myHeaders.append('Content-Type', 'application/json');

      var raw = JSON.stringify({
        username: username,
        password: password,
      });

      var requestOptions: Object = {
        method: 'POST',
        headers: myHeaders,
        body: raw,
        redirect: 'follow',
      };

      fetch('http://localhost:2324/signup', requestOptions)
        .then((response) => {
          if (response.ok) {
            return response.json();
          } else {
            this.usernameError = 'Username Already Exists';
            throw Error('Not OK');
          }
        })
        .then((result) => {
          if (result.token) {
            document.cookie = 'jwt=' + result.token;
            localStorage.setItem('user', JSON.stringify(result.user));
            this.router.navigate(['']);
          }
        })
        .catch((error) => console.log('error', error));
    }
  }
}
<div class="container py-5">
  <div
    class="col-12 col-sm-10 col-md-6 col-lg-4 m-auto border shadow rounded p-4"
  >
    <h3 class="mb-4 pb-2 border-bottom">User Signup</h3>
    <div class="mb-3">
      <label for="username" class="form-label">username</label>
      <input
        type="username"
        class="form-control"
        name="username"
        #username
        aria-describedby="usernameHelpId"
        placeholder="abc"
      />
      <small id="usernameHelpId" class="form-text text-danger">{{
        usernameError
      }}</small>
    </div>
    <div class="mb-3">
      <label for="" class="form-label">Password</label>
      <input
        type="password"
        class="form-control"
        name="password"
        #password
        aria-describedby="passwordHelpId"
        placeholder="Your Password"
      />
      <small id="passwordHelpId" class="form-text text-danger">{{
        passwordError
      }}</small>
    </div>
    <button
      type="button"
      class="btn btn-primary"
      #loginBtn
      (click)="getData(username.value, password.value)"
    >
      Login
    </button>
    <br />
    <hr />
    <a href="/login">Already Have An Account?</a>
  </div>
</div>
